sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	isDatePast: function (dateNeeded) {

		//Get current date and the When needed date
		var currentTime = new GlideDateTime();
		var whenNeededGDT = new GlideDateTime(dateNeeded);

		//Compare the dates and times. If the When needed is before today then abort action and show error message
		if (whenNeededGDT.before(currentTime)) {
			return true;
		} else {
			return false;
		}
	},

	isDateToday: function (dateNeeded) {

		//Get current date and the When needed date
		var today = new GlideDate();
		var whenNeeded = new GlideDateTime(dateNeeded).getDate();

		//Compare the dates and times. If the When needed is today then abort action and show error message
		if (today.getValue() === whenNeeded.getValue()) {
			return true;
		} else {
			return false;
		}

	},

	type: 'NeedItUtils'
};
